mapper.select:
SELECT*FROM property

	WHERE 1 = 1
	LIMIT #{offset}, #{pageSize}


	WHERE property_cate = #{property_cate}
	LIMIT #{offset}, #{pageSize}


	WHERE property_type = #{property_type}
	LIMIT #{offset}, #{pageSize}


	WHERE property_addr like '%${searchText}%'
	LIMIT #{offset}, #{pageSize}


	WHERE property_cate = #{property_cate} AND property_type =
	#{property_type}
	LIMIT #{offset}, #{pageSize}


	WHERE property_type = #{property_type} AND property_addr like
	'%${searchText}%'
	LIMIT #{offset}, #{pageSize}


	WHERE property_cate = #{property_cate} AND property_addr like
	'%${searchText}%'
	LIMIT #{offset}, #{pageSize}


	WHERE property_cate = #{property_cate} AND property_type =
	#{property_type} AND property_addr like '%${searchText}%'
	LIMIT #{offset}, #{pageSize}

SELECT count(*) FROM property
WHERE 1 = 1
WHERE property_cate = #{property_cate}
WHERE property_type = #{property_type}
WHERE property_addr like '%${searchText}%'
WHERE property_cate = #{property_cate} AND property_type = #{property_type}
WHERE property_type = #{property_type} AND property_addr like '%${searchText}%'
WHERE property_cate = #{property_cate} AND property_addr like '%${searchText}%'
WHERE property_cate = #{property_cate} AND property_type = #{property_type} AND property_addr like '%${searchText}%'

mapper.update:
UPDATE property SET
property_title=#{property_title}, property_cate=#{property_cate},
price=#{price}, deposit=#{deposit},
description=#{description}

,property_fname=#{property_fname}, property_realfname=#{property_realfname}, 
property_fsize=#{property_fsize}

WHERE property_id = #{property_id}

mapper.insert:
INSERT INTO property ( property_title, property_type, property_cate, property_addr, post_code, price, deposit, property_size, bedrooms, bathrooms, description,agent_id, property_year, reg_date, property_count,addr_detail
,property_fname, property_realfname, property_fsize
) VALUES ( #{property_title}, #{property_type}, #{property_cate}, #{property_addr}, #{post_code}, #{price}, #{deposit}, #{property_size}, #{bedrooms}, #{bathrooms}, #{description},#{agent_id}, #{property_year}, now(), #{property_count},#{addr_detail}
,#{property_fname}, #{property_realfname}, #{property_fsize}
)

mapper.select:
SELECT * FROM property
WHERE agent_id = #{agent_id}
LIMIT #{offset}, #{size}
